properties.body:
{% set locale = 'fr' %}

{% set path = {
    index: environment.alias,
    body: {
        query: {
            bool: {
                must: [{
                    terms: {  _contenttype: ['section'] },
                }, {
                    nested: {
                        path: 'paths',
                        query: {
                            bool: {
                                must: [{
                                    terms: { 'paths.uid': [source.menu] }
                                },{
                                    terms: { 'paths.locale': [locale] }
                                },]
                            }
                        }
                    },
                }],
            },
        },
    },
    _source: ['paths.*'],
    size: 1,
}|search.hits.hits[0]._source.paths|default([])|filter(p => p.uid == source.menu and p.locale == locale) %}

{% if path|length > 0 %}
    /channel/{{ environment.name }}/{{ locale }}/{{ path|first.path }}
{% else %}
    null
{% endif %}

