{% if source.avatar is defined %}
    <img src="{{ path('ems_asset_processor', {processor: 'preview', hash: source.avatar.sha1}) }}">
{% endif %}

extraOptions.postProcessing:
{% apply spaceless %}
    {% set data = _source.body|default('')|striptags|ems_html_decode %}
    {% if data|length > 100 %}
        {% set data = data[:80]~'...' %}
    {% endif %}
    
    {{ data|json_encode|raw }}

{% endapply %}

{% apply spaceless %}
    {% set data = _source.link|default('')|emsco_document_info %}
    {% if data %}
        {{ data.revision('preview').label|json_encode|raw }}
    {% else %}
        {{ cant_be_finalized('Form not found') }}
    {% endif %}
{% endapply %}

{% if _source.slug is not defined and _source.title is defined %}
    {% set slug = _source.title|ems_webalize %}
{% elseif _source.slug is defined %}   
    {% set slug = _source.slug %}
{% endif %}

{% if slug|default(false) %}
    {% set query = {
        'index': ('preview'|emsco_get_environment.alias),
        'size': 1,
        'type': 'page',
        'body': '{"query":{"bool":{"should":[{"term":{"de.slug":{"value":"' ~ slug ~ '"}}},{"term":{"en.slug":{"value":"' ~ slug ~ '"}}},{"term":{"fr.slug":{"value":"' ~ slug ~ '"}}},{"term":{"nl.slug":{"value":"' ~ slug ~ '"}}}],"must_not":[{"term":{"_id":{"value":"' ~ _id ~ '"}}}]}}}'
    }%}
            
    {% set searchResult = query|search %}
    
    {% if finalize|default(false) and searchResult.hits.hits|length > 0 %}
        {{ cant_be_finalized('The slug: ' ~ slug ~ ' already exists.') }}
    {% endif %}
    
    {{ slug|json_encode|raw }}
{% endif %}

displayOptions.valueTemplate:
{% set data = {} %}

{% for item in _source.blocks|default('{}')|ems_json_menu_nested_decode %}
     {% if item.object.label is defined %}
         {% set data = data|merge([item.object.label]) %}
     {% endif %}
     {% if item.object.body is defined %}
         {% set data = data|merge([item.object.body|striptags]) %}
     {% endif %}
     {% if item.object.value is defined %}
         {% set data = data|merge([item.object.value]) %}
     {% endif %}
     {% if attribute(item.object, 'sub-title') is defined %}
         {% set data = data|merge([attribute(item.object, 'sub-title')]) %}
     {% endif %}
{% endfor %}

{{ data|json_encode|raw }}

