extraOptions.postProcessing:
{% apply spaceless %}
    {% if finalize %}
        {% set paths = {} %}
        {% set config = 'config'|i18n('en')|json_decode %}
        {% for locale in config.locales %}
            {% if attribute(_source, locale) is defined %}
                {% set baseUrl = attribute(_source, locale).baseurl|default('') %}
                {% set structure = attribute(_source, locale).structure|default('{}')|ems_json_menu_nested_decode %}

                {% for item in structure|filter(item => item.type not in ['link', 'section']) %}
                    {% set path = [] %}

                    {% for p in item.path|filter(item => item.type not in ['link', 'section']) %}
                        {%- set path = path|merge([p.object.label|default('no-label')]) -%}
                    {% endfor %}

                    {% set paths = paths|merge([{
                        'uid': (item.id),
                        'path' : baseUrl ~ (path|map(p => p|ems_webalize)|join('/')|default('/')),
                        'locale' : locale,
                        'target' : (item.object.link|default(null)),
                    }]) %}

                {% endfor %}
            {% endif %}
        {% endfor %}

        {{ paths|default([])|json_encode|raw }}
    {% endif %}
{% endapply %}

options.template:
{% apply spaceless %}
    {%- set languages = [ 
        {'locale': 'en', 'label': 'English'}, 
        {'locale': 'fr', 'label': 'French'}, 
        {'locale': 'nl', 'label': 'Dutch'}, 
        {'locale': 'de', 'label': 'German'}
    ] -%}
    
    {%- set locale = app.user.localePreferred|default('fr') -%}
    {% set languages = [languages|filter(v => v.locale == locale)|first|default(null)]|merge(languages|filter(v => v.locale != locale))|filter(v => v != null) %}

    {%- set selectedLanguage = app.request.query.get('l', languages|first.locale) -%}
    
    {% set mustStructures = [
        { "term": { "_contenttype": { "value":"structure" } } }
    ] %}
    

    {%- set searchStructures = { 
        "index": ('preview'|emsco_get_environment.alias), 
        "size": 500, 
        "_source": [ ("#{selectedLanguage}.*"), "label", "color", "_finalization_datetime", "_finalized_by" ],
        "body": { "query": { "bool": { "must": (mustStructures) } } },
        "sort":[ {"order":{"order":"asc"} } ] 
    }|search.hits.hits -%}
    
    {% set columns = {
      "menu": { "label": "Menu", "width": "60%" },
      "last_update": { "label": "Last update", "width": "220px" },
      "is_live": { "label": "Live", "width": "100px" },
      "action": { "label": "Action", "width": "200px" },
    } %}
    
{% endapply %}


<div class="row">
    <div class="col-md-12">
        <form method="GET" id="form-filters" class="navbar-form navbar-left">
            <div class="form-group">
                <select name="l" id="select-language" class="selectpicker form-control input-sm">
                    {% for l in languages %}
                        <option value="{{ l.locale }}" {{ l.locale == selectedLanguage ? 'selected' }}>{{ l.label }}</option>
                    {% endfor %}
                </select> 
            </div>
        </form>
    </div>
</div>
<hr style="border-color: #ccc; margin: 0;" />

<div class="row">
    <div class="col-md-12">
        <div class="panel-group mb-2"  role="tablist" aria-multiselectable="true">
            <div class="panel">
                <div class="panel-heading f-heading" role="tab">
                    {% for c in columns %}
                        <div class="f-item" style="width: {{ c.width }};"><strong>{{ c.label }}</strong></div>
                    {% endfor %}
                </div>
            </div>
        </div>
        {% for i, h in searchStructures %}
            {% set docInfo = [h._source._contenttype, h._id]|join(':')|emsco_document_info %}
            {% set revisionId = h._id|get_revision_id(h._source._contenttype) %}
            {% set liveId = 3 %}
            {% set previewId = 2 %}
            <div class="panel-group mb-2"  role="tablist" aria-multiselectable="true">
                <div class="panel panel-default">
                    <div class="panel-heading f-heading" style="border-bottom: 1px solid #ccc;" role="tab">
                        {% for column, c in columns %}
                            <div class="f-item" style="width: {{ c.width }};">
                                {% if column == "menu" %}
                                    <a role="button" data-toggle="collapse" data-parent="#structure-{{ i }}" href="#structure-{{ i }}" aria-expanded="false" aria-controls="structure-{{ i }}">
                                        <i class="fa fa-plus-circle" style="color: #000000;" aria-hidden="true"></i>&nbsp; 
                                        {{ attribute(h._source, selectedLanguage).title|default(h._source.label)  }}
                                    </a>
                                {% elseif column == "last_update" %}
                                    <div data-toggle="tooltip" data-placement="top" aria-hidden="true" title="By {{ h._source._finalized_by|default("N.A.") }}" >
                                        {{ h._source._finalization_datetime|default ? h._source._finalization_datetime|date("d/m/Y (H:i)") : "N.A." }}
                                    </div>
                                {% elseif column == "is_live" %}
                                    <i style="color: {{ docInfo.published('live') and docInfo.aligned('live') ? 'green' : 'red' }}" 
                                        class="{{ docInfo.published('live') and docInfo.aligned('live') ? 'fa fa-check' : 'fa fa-eye-slash' }}"></i>
                                {% elseif column == 'action' %}
                                    {% set revisionUrl = path("ems_revision_edit", {revisionId: revisionId}) %}
                                    {% set redirectUrl = path("data.customindexview", {viewId: view.id } ) %}
                                    {% set newDraftUrl = path("revision.new-draft", {type: h._source._contenttype, ouuid: h._id }) %}
                                    {% set publishUrl =  path("revision.publish_to", {revisionId: revisionId, envId: liveId, redirectToUrl: redirectUrl }) %}
                                
                                    <div class="btn-group btn-group-sm" style="width: 130px;">
                                      <a href="{{ docInfo.draft ? revisionUrl : (docInfo.aligned("live") ? newDraftUrl : publishUrl) }}" class="btn btn-default" style="width: 100px;">
                                        {{ docInfo.draft ? "Edit draft" : docInfo.aligned("live") ? "Edit" : "Publish in live" }}
                                      </a>
                                      <button type="button" class="btn btn-default dropdown-toggle" data-toggle="dropdown" aria-expanded="false">
                                        <span class="caret">
                                        </span>
                                        <span class="sr-only">Toggle Dropdown
                                        </span>
                                      </button>
                                      <ul class="dropdown-menu" role="menu">
                                        <li>
                                          <a href="{{ path("data.revisions", {ouuid: h._id, type: h._source._contenttype} ) }}">Revision</a>
                                        </li>
                                        {% if docInfo.draft %}
                                            <li><a href="{{ path("revision.publish_to", {revisionId: revisionId, envId: previewId}) }}">Publish in preview</a></li>
                                        {% elseif not docInfo.aligned('live') %}
                                            <li><a href="{{ newDraftUrl }}">Edit</a></li>
                                        {% endif %}
                                      </ul>
                                    </div>
                                    
                                {% endif %}
                            </div>
                        {% endfor %}
                    </div>
                    <div id="structure-{{ i }}" class="panel-collapse collapse collapse-info" role="tabpanel" aria-labelledby="structure-{{ i }}">
                        <div>
                            {% set structure = attribute(h._source, selectedLanguage).structure|default(false) %}
    
                            {% if attribute(h._source, selectedLanguage).overview is defined %}
                                <div style="padding: 10px 20px 0 10px;">{{ attribute(h._source, selectedLanguage).overview|raw }}</div>
                            {% endif %}
    
                            {% if structure  %}
                                {{ emsco_json_menu_nested({
                                    'id': h._id,
                                    'document': h,
                                    'field': 'structure',
                                    'field_document': ("#{selectedLanguage}.structure"),
                                    'structure': (structure),
                                    'actions': {
                                        'add': { 'deny': ['all'] },
                                        'edit': { 'deny': ['all'] },
                                        'delete': { 'deny': ['all'] },
                                        'move': { 'deny': ['all'] },
                                        'copy': { 'deny': ['all'] },
                                        'paste': { 'deny': ['all'] },
                                        'preview': { },
                                     }
                                }) }}
                            {% endif %}
                        </div>
                    </div>
                 </div>
            </div>
        {% endfor %}
    </div>
</div>
