{% extends '@EMSCH/template/variables.twig' %}
{% trans_default_domain trans_default_domain %}

{% block request %}
    {% apply spaceless %}
        {% set response = {
            "name": 'site.name'|trans,
            "short_name": 'site.short_name'|trans,
            "icons": [{
                "src": emsch_asset('img/head/icon.png', {
                    _config_type: 'image',
                    _width: 192,
                    _height: 192,
                    _quality: 0,
                }),
                "sizes": "192x192",
                "type": "image/png"
            }, {
                "src": emsch_asset('img/head/icon.png', {
                    _config_type: 'image',
                    _width: 512,
                    _height: 512,
                    _quality: 0,
                }),
                "sizes": "512x512",
                "type": "image/png"
            }],
            "theme_color": "#ffffff",
            "background_color": "#ffffff",
            "display": "standalone"
        } %}
        {{ response|json_encode|raw }}
    {% endapply %}
{% endblock %}
